package com.test;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=10;
int c;

c=a; //==   c=10 ;

c+=a; // c=c+a  , 20

c-=a; // c=c-a  , 10

c*=a; // c=c*a , 100

System.out.println("value of a "+a);
System.out.println("value of c "+c);



	}

}


