package com.test;

public class ForLoopExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		for (int a=10; a>0;a--) {
			System.out.println("a value"+a);
		System.out.println("------------");
		}
		
		for(int b=10;;b++) {
			System.out.println("b value"+b);
			
		}
		
		
		
	}

}
